package ExceptionHandling;

public class ExceptionHandling1demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arr[] = {1,2,3};
		try {
			for (int i = 0; i <= arr[i]; i++) {
				System.out.println(arr[i]);
				}
			
			}
		catch(Exception e) {
			System.out.println(e);
			//e.printStackTrace();
		}
		
	}

}
